class Solution {
private:

    void dfs(const vector<vector<int>>& graph, const int& n, int i, vector<bool>& visit, int& nodeNum) {
        visit.at(i) = true;
        nodeNum += 1;

        for (auto node: graph.at(i)) {
            if (visit.at(node) == false) {
                dfs(graph, n, node, visit, nodeNum);
            }
        }
    }

public:

    long long countPairs(int n, vector<vector<int>>& edges) {
        if (edges.size() == 0 && n == 1) {
            return 0;
        }

        vector<vector<int>> graph (n);

        for (auto edge: edges) {
            graph.at(edge.at(0)).push_back(edge.at(1));
            graph.at(edge.at(1)).push_back(edge.at(0));
        }

        vector<int> componentNode;
        vector<bool> visit (n, false);

        for (int i {0}; i < n; ++i) {
            if (visit.at(i) == false) {
                visit.at(i) = true;
                int nodeNum {0};
                dfs(graph, n, i, visit, nodeNum);
                componentNode.push_back(nodeNum);
            }
        }

        long long ans = ((long long)n*((long long)n-1))/2;
        for (int i {0}; i < componentNode.size(); ++i) {
            ans -= (((long long)componentNode.at(i)*((long long)componentNode.at(i)-1)/2));
        }

        return ans;
    }
};

